import java.text.NumberFormat;
import java.util.Scanner;
public class JIfElseWages1
{
	public static void main (String[] args)
	{
		//Step 1: Define the variables that we need for this program
		final double RATE = 8.25; 
		final int STANDARD = 40; 
		double wage = 0.0;
		//Step 2: Read the number of hours that the employee has worked
		Scanner scan = new Scanner (System.in);
		System.out.println("Enter the number of hours that the employee has worked: ");
		int hours = scan.nextInt();
		scan.close();
		//Step 3: Pay overtime at "time and a half" of the regular rate of pay
		if (hours > STANDARD) {
			wage = STANDARD * RATE + (hours-STANDARD) * (RATE * 1.5);
		} else {
			wage = hours * RATE;
		}
		//Step 4: Print the calculated wage
		NumberFormat fmt = NumberFormat.getCurrencyInstance();
		System.out.println("Wage: " + fmt.format(wage));
	}
}

import java.text.NumberFormat;
import java.util.Scanner;
public class JIfElseWages2
{
	public static void main (String[] args)
	{
		//Step 1: Define the variables that we need for this program
		final double RATE = 8.25; 
		final int STANDARD = 40; 
		final int WEEKEND_PAY_MIN = 30; 
		final int WEEKEND_PAY_MAX = 50; 
		double wage = 0.0;
		//Step 2: Read the input data
		Scanner scan = new Scanner (System.in);
		System.out.println("Enter the number of hours that the employee has worked: ");
		int hours = scan.nextInt();
		System.out.println("Enter the number of days that the employee has worked during weekends: ");
		int noWeekendDays = scan.nextInt();
		scan.close();
		//Step 3: Pay overtime at "time and a half" of the regular rate of pay
		if (hours > STANDARD) {
			wage = STANDARD * RATE + (hours-STANDARD) * (RATE * 1.5);
		} else {
			wage = hours * RATE;
		}
		//Step 4: Take into account the extra pay for the work during weekends days
		if (noWeekendDays < 5) {
			wage += (noWeekendDays * WEEKEND_PAY_MIN);
		} else {
			wage += (noWeekendDays * WEEKEND_PAY_MAX);
		}
		//Step 5: Print the calculated wage
		NumberFormat fmt = NumberFormat.getCurrencyInstance();
		System.out.println("Wage: " + fmt.format(wage));
	}
}

