import java.io.*;
import java.util.Scanner;
public class JInputStat1 {
	public static void main(String[] args) {
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the name of the file:");
		String name = scan.next();
		JInputStat1 i1 = new JInputStat1();
		i1.getStat(name);
	}
	//Step 1: Define the method
	public void getStat(String file_name) {
		//Step 1.1: Define the variables that we need for reporting the file information
		int numLines = 0;
		String longestLine = "";
		//Step 1.2: Enclose the code that might throw an exception within the try block
		try {
			//Step 1.2.1: Wrap a BufferedReader around a FileReader
			FileReader fr = new FileReader(file_name); 
			BufferedReader br = new BufferedReader(fr); 
			//Step 1.2.2: Use the BufferedReader object to read one line at a time
			String line = null;
			while ( ( line = br.readLine() ) != null) {
				numLines += 1;
				String[] tokens = line.split(" ");
				int numWords = tokens.length;
				//Determine if the line is the longest line so far
				if (line.length() > longestLine.length()) {
					longestLine = line;
				}
				//Print the line information
				System.out.println("Line " + numLines + " has " + numWords + " tokens ");
			}
			System.out.println("Longest line:" + longestLine);
			//Step 1.2.3: Close the BufferedReader object attached to the file
			br.close();
		}
		//Step 1.3: Handle all possible exceptions that may be thrown in the try block 
		catch (FileNotFoundException ex) {
			System.out.println("File not found");
		} catch (IOException ex ) {
			System.out.println("Problem with the file!");
		}
	}
}

