def partition(num_list, start, end):
    pivot = num_list[start]
    left = start
    right = end

    while left < right:
        while num_list[left] < pivot:
            left += 1
        while num_list[right] > pivot:
            right -= 1    
        if left < right:
            temp = num_list[left]
            num_list[left] = num_list[right]
            num_list[right] = temp
    return num_list

