public static int factorial(int n) {
if (n == 0)
return 1;
else
return n*factorial(n-1);
}

// Recursively computes the nth Fibonacci number.  The Fibonacci sequence
//  starts with 0, 1, and each subsequent number is computed by adding the
//  two previous numbers.
// (The "0th" Fibonacci number is 0, and the "1st" Fibonacci number is 1.)
public static int fibo(int n) {
if (n == 0)
return 0;
else if (n == 1)
return 1;
else
return fibo(n-1) + fibo(n-2);
}

public static void main(String[] args) {
// The recursive Fibonacci method is very inefficient!
for (int i = 0; i < 100; i++)
System.out.println(fibo(i));

}
}

/*
* Checks whether a user-entered integer is prime.
* A prime number is divisible by only itself and 1.
*  2 is the smallest (and the only even) prime number.
*/
import java.util.Scanner;

public class PrimeChecker {

public static void main(String[] args) {
Scanner s = new Scanner(System.in);

// Get user input for a number to check
System.out.println("Enter a number to check:");
int num = s.nextInt();

boolean divisorFound = false;
for (int d = 2; d < num; d++) {
if (num % d == 0) {
divisorFound = true;
break;
}
}

// If we make it through the entire loop without finding a working divisor, divisorFound
//  will still have its original value of false.

if (!divisorFound) {
System.out.println(num + " is prime!");
} else {
System.out.println(num + " is not prime!");
}
}
}

