import java.util.Scanner;
public class FahrenheitToCelsius {
	public static void main(String[] args) {
		//Step 1: Define the data that we need for this program
		final int BASE = 32;
		final double CONVERSION_FACTOR = 5.0 / 9;
		//Step 2: Read the input Fahrenheit value
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the Fahrenheit value: ");
		int fahrenheitTemp = scan.nextInt();
		scan.close();
		//Step 3: Compute the Celsius equivalent of the Fahrenheit value
		double celsiusTemp = (fahrenheitTemp - BASE) * CONVERSION_FACTOR ;
		System.out.println("Fahrenheit Temperature: " + fahrenheitTemp);
		System.out.println("Celsius Equivalent: " + celsiusTemp);
	}
}

import java.util.Scanner;
public class PythagoreanTheorem2 {
	public static void main(String[] args) {
		//Step 1: Receive the values for the first side of a right-angle triangle and the hypotenuse
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the length of side A:");
		double sideA = scan.nextDouble();
		System.out.println("Enter the length of the hypotenuse:");
		double hypotenuse = scan.nextDouble();
		scan.close();
		//Step 2: Calculate square of side A
		double squareSideA = Math.pow(sideA, 2);
		//Step 3: Calculate square of hypotenuse
		double squareHypotenuse = Math.pow(hypotenuse, 2);
		//Step 4: Use Pythagorean theorem to calculate the length of the triangle's other adjacent side
		double sideB = Math.sqrt(squareHypotenuse - squareSideA);
		//Step 4: Print the result
		System.out.println("Given that side A is " + sideA + " and the hypotenuse is " + hypotenuse + ", side B is " + sideB);
	}
}

