		Scanner scan = new Scanner(System.in);
		System.out.println("Enter an integer (0 to quit): ");
		int num = scan.nextInt();
		//Step 3: Process the integer that the user has entered, then receive and process the next integers as long as the user enters a non-zero integer; otherwise stop
		while (num != 0) {
			if (num % 2 == 0) {
				count++;
				total += num;
			}
			System.out.println("The sum of even numbers so far is " + total+", count of even numbers = "+count);
			System.out.println("Enter an integer (0 to quit): ");
			num = scan.nextInt();   
