public class JSumDigits {
	public static void main(String[] args) {
		//Step 1: Define the variables that we need for this program
		int num = 1234;
		int lastDigit;
		int total = 0;
		//Step 2: Sum up the digits of the integer 
		do {
			//Step 2.1: Get the last digit in the integer
			lastDigit = num % 10;
			//Step 2.2:Add the last digit to the sum of digits so far
			total = total + lastDigit;
			//Step 2.3: Remove the last digit from the integer
			num = num / 10;
			System.out.println("last digit: "+lastDigit+", sum: "+total+", integer: "+num);
		} while (num > 0);
		System.out.println("The sum of the digits: " + total);
	}
}

public class JReverseNumber {
	public static void main(String[] args) {
		//Step 1: Define the variables that we need for this program
		int num = 1234;
		int lastDigit;
		int reverse = 0;
		//Step 2: Reverse the digits of the integer mathematically
		do {
			//Step 2.1: Get the last digit in the integer
			lastDigit = num % 10;
			//Step 2.2:Append the last digit to reverse
			reverse = (reverse * 10) + lastDigit;
			//Step 2.3: Remove the last digit from the integer
			num = num / 10;
			System.out.println("last digit: "+lastDigit+", reverse: "+reverse+", integer: "+num);
		} while (num > 0);
		System.out.println("The reversed integer is " + reverse);
	}
}

