public class Transactions2 {
	public static void main (String[] args) {
		Account2 acct1 = new Account2("Tina Murphy", 72354, 25.59);
		Account2 acct2 = new Account2("Angelica Adams", 69713, 500.00);
		Account2 acct3 = new Account2("Edward Demsey", 93757, 769.32);
		Account2 acct4 = new Account2("Samuel Williams", 300301);
		acct1.deposit (44.10);  
		acct4.deposit (5.00);  
		double adamsBalance = acct2.deposit (75.25);
		System.out.println (acct2.getName() + "'s balance after deposit: " + adamsBalance);
		System.out.println (acct2.getName() + "'s balance after withdrawal: " + acct2.withdraw (480, 1.50));
		acct3.withdraw (-100.00, 1.50);  
		acct1.addInterest();
		acct2.addInterest();
		acct3.addInterest();
		System.out.println (acct1);
		System.out.println (acct2);
		System.out.println (acct3);
		System.out.println (acct4);
	}
}
class Account2 {
	//Step 1: Declare the instance variables 
	private final double RATE = 0.035;  
	private String acctName;
	private long acctNumber;
	private double balance;
	//Step 2: Define the constructor to set up an account with the specified account name, account number, and initial balance
	public Account2 (String owner, long account, double initial) {
		acctName = owner;
		acctNumber = account;
		balance = initial;
	}
	//Step 3: Define the constructor to set up an account with the specified account name and account number
	public Account2 (String owner, long account) {
		this(owner, account, 0);
	}
	//Step 4: Define the methods to perform various services on the account
	public double deposit (double amount) {
		if (amount > 0) {
			balance = balance + amount;
		}
		return balance;
	}
	public double withdraw (double amount, double fee) {
		if (amount > 0 && fee >= 0 && amount+fee < balance) {
			balance = balance - amount - fee;
		}
		return balance;
	}
	public double addInterest () {
		balance += (balance * RATE);
		return balance;
	}
	//Step 5: Define the methods to get the current state of the account 
	public String getName() {
		return acctName;
	}
	public double getBalance() {
		return balance;
	}
	public long getAcctNumber() {
		return acctNumber;
	}
	public String toString () {
		NumberFormat fmt = NumberFormat.getCurrencyInstance();
		return (acctNumber + "\t" + acctName + "\t" + fmt.format(balance));
	}
}

