import java.util.*;
import java.io.*;
public class JVocabulary2 {
	public static void main(String[] args) throws FileNotFoundException {
		JVocabulary2 v2 = new JVocabulary2();
		ArrayList<String> lst1 = v2.getWords("test1.txt");
		System.out.println("list1 words = " + lst1);
		ArrayList<String> lst2 = v2.getWords("test2.txt");
		System.out.println("list2 words = " + lst2);
		v2.compareLists(lst1, lst2);
	}
	//Step 1: Define the method that compares two lists to find the largest one
	public void compareLists(ArrayList<String> list1, ArrayList<String> list2 ) {
		if ( list1.size() > list2.size() ) {
			System.out.println("list1 has more words.");
		} else if ( list2.size() > list1.size() ) {
			System.out.println("list2 has more words");
		} else {
			System.out.println("list1 and list2 have the same number of words.");
		}
	}
	//Step 2: Define the method that creates a list of words from a file
	public ArrayList<String> getWords(String fileName) {
		//Step 2.1: Create an empty list to store the words
		ArrayList<String> words = new ArrayList<String>();
		//Step 2.2: Enclose the code that might throw an exception within the try block
		try {
			//Step 2.2.1: Wrap a BufferedReader around a FileReader
			FileReader fr = new FileReader(fileName); 
			BufferedReader br = new BufferedReader(fr); 
			//Step 2.2.2: Use the BufferedReader object to read one line at a time
			String line = null;
			while ( ( line = br.readLine() ) != null) {
				//Split the line into words, ignoring the case of the letters
				String[] tokens = line.toLowerCase().split(" ");
				//Add each word in the line to the list
				for (String token : tokens) {
					words.add(token);
				}
			}
			//Step 2.2.3: Close the BufferedReader object attached to the file
			br.close();
		//Step 2.3: Handle all possible exceptions that may be thrown in the try block 
		} catch (FileNotFoundException ex) {
			System.out.println("File not found");
		} catch (IOException ex ) {
			System.out.println("Problem with the file!");
		}
		//Step 2.4: return the list
		return words;
	}
}

