import java.text.NumberFormat;
public class Transactions1 {
	public static void main (String[] args) {
		Account1 acct1 = new Account1("Tina Murphy", 72354, 25.59);
		Account1 acct2 = new Account1("Angelica Adams", 69713, 500.00);
		Account1 acct3 = new Account1("Edward Demsey", 93757, 769.32);
		acct1.deposit (44.10);  
		double adamsBalance = acct2.deposit (75.25);
		System.out.println (acct2.getName() + "'s balance after deposit: " + adamsBalance);
		System.out.println (acct2.getName() + "'s balance after withdrawal: " + acct2.withdraw (480, 1.50));
		acct3.withdraw (-100.00, 1.50);  
		acct1.addInterest();
		acct2.addInterest();
		acct3.addInterest();
		System.out.println (acct1);
		System.out.println (acct2);
		System.out.println (acct3);
	}
}
class Account1 {
	//Step 1: Declare the instance variables 
	private final double RATE = 0.035;  
	private String acctName;
	private long acctNumber;
	private double balance;
	//Step 2: Define the constructor to set up an account with the specified account name, account number, and initial balance
	public Account1 (String owner, long account, double initial) {
		acctName = owner;
		acctNumber = account;
		balance = initial;
	}
	//Step 3: Define the methods to perform various services on the account
	public double deposit (double amount) {
		if (amount > 0) {
			balance = balance + amount;
		}
		return balance;
	}
	public double withdraw (double amount, double fee) {
		if (amount > 0 && fee >= 0 && amount+fee < balance) {
			balance = balance - amount - fee;
		}
		return balance;
	}
	public double addInterest () {
		balance += (balance * RATE);
		return balance;
	}
	//Step 4: Define the methods to get the current state of the account 
	public String getName() {
		return acctName;
	}
	public double getBalance() {
		return balance;
	}
	public long getAcctNumber() {
		return acctNumber;
	}
	public String toString () {
		NumberFormat fmt = NumberFormat.getCurrencyInstance();
		return (acctNumber + "\t" + acctName + "\t" + fmt.format(balance));
	}
}

import java.text.NumberFormat;
import java.util.Scanner;
public class LoanTester1 {
	public static void main(String[] args) {
		Scanner input = new Scanner(System.in);
		System.out.println("Enter the annual interest rate: ");
		double annualInterestRate = input.nextDouble();
		System.out.println("Enter the number of years for the loan period: ");
		int numberOfYears = input.nextInt();
		System.out.println("Enter the loan amount: ");
		double loanAmount =  input.nextDouble();
		Loan1 loan = new Loan1(annualInterestRate, numberOfYears, loanAmount);
		NumberFormat fmt = NumberFormat.getCurrencyInstance();
		System.out.println("The monthly paymenst for the loan is: " + fmt.format(loan.getMonthlyPayment()));
	}
}
class Loan1 {
	//Step 1: Declare the instance variables 
	private double annualInterestRate;
	private int numberOfYears;
	private double loanAmount;
	//Step 2: Define the constructor to create a loan with the specified values 
	public Loan1(double annualInterestRate, int numberOfYears, double loanAmount) {
		this.annualInterestRate = annualInterestRate;
		this.numberOfYears = numberOfYears;
		this.loanAmount = loanAmount;
	}
	//Step 3: Define the constructor to create a loan without specifying any value 
	public Loan1() {
		this(0.03, 1, 1000);
	}
	//Step 4: Define the method that calculates the amount of monthly payments on the loan
	public double getMonthlyPayment() {
		double monthlyInterestRate = annualInterestRate / 12;
		double monthlyPayment = loanAmount * monthlyInterestRate / (1 -
				(1 / Math.pow(1 + monthlyInterestRate, numberOfYears * 12)));
		return monthlyPayment;    
	}
	//Step 5: Define the methods to get/set the properties of the loan
	public double getAnnualInterestRate() {
		return annualInterestRate;
	}
	public void setAnnualInterestRate(double annualInterestRate) {
		this.annualInterestRate = annualInterestRate;
	}
	public int getNumberOfYears() {
		return numberOfYears;
	}
	public void setNumberOfYears(int numberOfYears) {
		this.numberOfYears = numberOfYears;
	}
	public double getLoanAmount() {
		return loanAmount;
	}
	public void setLoanAmount(double loanAmount) {
		this.loanAmount = loanAmount;
	}
}

