int binarySearch( const int b[], int searchKey, int low, int high )
{
int middle;
while ( low <= high ) {
middle = ( low + high ) / 2;
printRow( b, low, middle, high );

if ( searchKey == b[ middle ] ) {
return middle;
}
else if ( searchKey < b[ middle ] ) {
high = middle - 1;
}

else {
low = middle + 1;
}
}
return -1;
}

