void bubbleSort( int * const array, const int size )
{
void swap( int *element1Ptr, int *element2Ptr );
int pass;
int j;
for ( pass = 0; pass < size - 1; pass++ ) {
for ( j = 0; j < size - 1; j++ ) {
if ( array[ j ] > array[ j + 1 ] ) {
swap( &array[ j ], &array[ j + 1 ] );
}
}
}
}

void swap( int *element1Ptr, int *element2Ptr )
{
int hold = *element1Ptr;
*element1Ptr = *element2Ptr;
*element2Ptr = hold;
}

