void encrypt(char sMessage[], int random)
{
int x = 0;

while ( sMessage[x] != '\0' ) {
sMessage[x] += random;
x++;
} //end loop

x = 0;
printf("\nEncrypted Message is: ");
while ( sMessage[x] != '\0' ) {
printf("%c", sMessage[x]);
x++;
} //end loop
}  //end encrypt function

void decrypt(char sMessage[], int random)
{
int x = 0;
x = 0;
while ( sMessage[x] != '\0' ) {
sMessage[x] = sMessage[x] - random;
x++;
} //end loop

x = 0;
printf("\nDecrypted Message is: ");
while ( sMessage[x] != '\0' ) {
printf("%c", sMessage[x]);
x++;
} //end loop

}  //end decrypt function

