    def __init__(self):
        self.items = []
    # Checking if the stack is empty
    def is_empty(self):
        return len(self.items) == 0
    # Adding elements to the stack
    def push(self, item):
        self.items.append(item)
    # Removing the elements from the stack
    def pop(self):
        return self.items.pop()
