def binary_search(data, key, low, high):
    # Step 1.1: Set default values
    guess = (high + low) // 2
    # Step 1.2: Set terminating condition(s)
    if low >= high:
        return -1
    elif key == data[guess]:
        return data.index(key)
    # Step 1.3: Set recursion condition
    else:
        if (key < data[guess]):
            high = guess
        else:
            low = guess + 1
        return binary_search(data, key, low, high)
