public class PythagoreanTheorem1 {
	public static void main(String[] args) {
		//Step 1: Receive the values for each side of a right-angle triangle
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the length of side A:");
		double sideA = scan.nextDouble();
		System.out.println("Enter the length of side B:");
		double sideB = scan.nextDouble();
		scan.close();
		//Step 2: Calculate square of side A
		double squareSideA = Math.pow(sideA, 2);
		//Step 3: Calculate square of side B
		double squareSideB = Math.pow(sideB, 2);
		//Step 4: Use Pythagorean theorem to calculate the length of the triangle's hypotenuse
		double hypotenuse = Math.sqrt(squareSideA + squareSideB);
		//Step 5: Print the result
		System.out.println("Given that side A is " + sideA + " and side B is " + sideB + ", the hypotenuse is " + hypotenuse);
	}
}

