public class JArrayRotateRight {
	//Step 1: Define the method for rotating the array to the right by one position
	public static int[] rotateRight(int[] lst) {
		//Step 1.1: Store the value at the end of the array in a local variable
		int last = lst[lst.length - 1];
		//Step 1.2: Rotate the remaining elements of the array to the right 
		for (int i = lst.length - 1; i > 0; i--) {
			lst[i] = lst[i - 1];
		}
		//Step 1.3: Move the value at the end to the front of the array
		lst[0] = last;
		//Step 1.4: Return the rotated array
		return lst;
	}
	//Step 2: Define the method for printing the array
	public static void print(int[] array) {
		for (int x : array) {
			System.out.print(x + " ");
		}
		System.out.println();
	}
	public static void main(String[] args) {
		int[] values = {3, 8, 9, 8, 7, 5};
		System.out.println("Original Array:");
		print(values);
		System.out.println("Rotated Array:"); 
		print(rotateRight(values));
	}
}

