		int last = lst[lst.length - 1];
		int secondLast = lst[lst.length - 2];
		//Step 1.2: Rotate the remaining elements of the array to the right by 2 position
		for (int i = lst.length - 1; i > 1; i--) {
			lst[i] = lst[i - 2];
		}
		//Step 1.3: Move the last two values of the array to the front of the array
		lst[0] = secondLast;
		lst[1] = last;
