		Scanner scan = new Scanner(System.in);
		System.out.println("Enter the number of temperature values that will be entered:");
		int numDays = scan.nextInt();
		//Step 2: Read the temperature values and update the running total
		double[] temps = new double[numDays];
		int total = 0;
		for (int i = 0; i < numDays; i++) {
