import java.util.Scanner;
public class VendingMachine1
{
	public static void main(String[] args)
	{
		//Step 1: Define the constants that we need for this program
		final int PENNIES_PER_DOLLAR = 100;
		final int PENNIES_PER_QUARTER = 25;
		//Step 2: Read the bill value and item price
		Scanner scan = new Scanner(System.in);
		System.out.println("Enter bill value in dollars (1 = $1 bill, 5 = $5 bill, etc.): ");
		int billValue = scan.nextInt();
		System.out.println("Enter item price in pennies: ");
		int itemPrice = scan.nextInt();
		scan.close();
		//Step 3: Compute the change due
		int changeDue = PENNIES_PER_DOLLAR * billValue - itemPrice;
		//Step 4: Compute the number of dollars and update the change due after taking away the dollars
		int dollars = changeDue / PENNIES_PER_DOLLAR;
		changeDue = changeDue % PENNIES_PER_DOLLAR;
		//Step 5: Compute the number of quarters in the remaining change due
		int quarters = changeDue / PENNIES_PER_QUARTER;
		//Step 6: Display the result
		System.out.println("Your change consists of:"); 
		System.out.println(dollars + " dollars");
		System.out.println(quarters + " quarters");
	}
}

