	public void checkAge(String s) {
		//Step 1.1: Split the given string into the name and age 
		String[] lst = s.split(":");
		//Step 1.2: Enclose the code that might throw an exception within the try block
		try {
			String name = lst[0];
			int age = Integer.parseInt(lst[1]);
			if ( age >= 13 && age <= 19 ) {           
				System.out.println(name + " is a teenager.");
			} else {          
				System.out.println(name + " is not a teenager.");
			}
