# Step 2: Define the Singly Linked List class
class SLinkedList:
    def __init__(self):
        self.head = None
        self.size = 0
    def __str__(self):
        s = '['
        i = 0
        current = self.head
        while current != None:
            if i > 0:
                s = s + ','
            dataObject = current.getData()
            if dataObject != None:
                s = s + '%s' % dataObject
                i = i + 1
            current = current.getNext()
        s = s + ']'
        return s
    def add(self, item):
        temp = SLinkedListNode(item, None)
        temp.setNext(self.head)
        self.head = temp
        self.size += 1
