        self.__acct_number = account
        self.__balance = initial
    #Step 1.2: Define a method to provide a one-line description of the account
    def __str__(self) :
        return "{:d}{:>20s}{:>10.2f}".format(self.__acct_number, self.__acct_name, self.__balance)
